<?php/*** @package porto-child*/global $porto_settings, $page_share;$post_layout = 'grid';$columns = $porto_settings['grid-columns'];global $porto_blog_columns, $porto_post_style;if ($porto_blog_columns)    $columns = $porto_blog_columns;$post_style = $porto_post_style ? $porto_post_style : $porto_settings['post-style'];$post_class = array();$post_class[] = 'post post-' . $post_layout;switch ($columns) {    case '1': $post_class[] = ' col-sm-12'; break;    case '2': $post_class[] = ' col-sm-6'; break;    case '3': $post_class[] = ' col-sm-6 col-md-4'; break;    case '4': $post_class[] = ' col-sm-6 col-md-4 col-lg-3'; break;    default: $post_class[] = ' col-sm-6 col-md-4'; break;}if ($porto_settings['post-title-style'] == 'without-icon')    $post_class[] = 'post-title-simple';$post_share = get_post_meta($post->ID, 'post_share', true);$social_share = false;?><article <?php post_class($post_class); ?>>    <?php    if ($post_style == 'related') :        get_template_part('content', 'productc-item');    else :        ?>        <div class="grid-box">            <?php            // Post Slideshow            $slideshow_type = get_post_meta($post->ID, 'slideshow_type', true);            if (!$slideshow_type)                $slideshow_type = 'images';            if ($slideshow_type != 'none') : ?>                <?php if ($slideshow_type == 'images') :                    $featured_images = porto_get_featured_images();                    $image_count = count($featured_images);                    if ($image_count) :                        ?>                        <div class="post-image<?php if ($image_count == 1) echo ' single'; ?>">                            <div class="post-slideshow porto-carousel owl-carousel">                                <?php                                foreach ($featured_images as $featured_image) {                                    $attachment = porto_get_attachment($featured_image['attachment_id']);                                    if ($attachment) {                                        ?>                                        <div>                                            <div class="img-thumbnail">                                                <?php echo $porto_settings['post-link'] ? '<a href="'.get_the_permalink().'">' : '' ?>                                                <img class="owl-lazy img-responsive" width="<?php echo $attachment['width'] ?>" height="<?php echo $attachment['height'] ?>" data-src="<?php echo $attachment['src'] ?>" alt="<?php echo $attachment['alt'] ?>" />                                                <?php echo $porto_settings['post-link'] ? '</a>' : '' ?>                                                <?php if ($porto_settings['post-zoom']) : ?>                                                    <span class="zoom" data-src="<?php echo $attachment['src'] ?>" data-title="<?php echo $attachment['caption']; ?>"><i class="fa fa-search"></i></span>                                                <?php endif; ?>                                            </div>                                        </div>                                        <?php                                    }                                }                                ?>                            </div>                        </div>                        <?php                    else :?>                        <div class="post-image single">                            <div class="post-slideshow porto-carousel">                                <div>                                    <div class="img-thumbnail">                                        <?php                                        $imageid = 509;                                        $attachment = porto_get_attachment($imageid);                                        ?>                                        <img class="owl-lazy img-responsive" width="<?php echo $attachment['width'] ?>" height="<?php echo $attachment['height'] ?>" data-src="<?php echo $attachment['src'] ?>" alt="<?php echo $attachment['alt'] ?>" />                                    </div>                                </div>                            </div>                        </div>                    <?php endif;                endif;            endif;            ?>            <div class="post-content">                <h4 class="entry-title"><a href="<?php the_permalink() ?>"><?php the_title() ?></a></h4>            </div>            <div class="clearfix box-btn">                <a class="btn btn-xs btn-primary" href="<?php echo esc_url( apply_filters( 'the_permalink', get_permalink() ) ) ?>"><?php _e('More Information', 'porto-child') ?></a>            </div>        </div>    <?php endif; ?></article>