<?php/*** @package porto-child*/get_header();global $porto_settings, $wp_query;$term = $wp_query->queried_object;$term_id = $term->term_id;?><div id="content" role="main">    <?php if (category_description()) : ?>        <div class="page-content">            <?php echo category_description() ?>        </div>    <?php endif; ?>    <?php if (have_posts()) :?>        <?php if($term->count > 1) :?>            <div class="blog-posts posts-grid">                <div class="grid row">                    <?php                    while (have_posts()) {                        the_post();                        get_template_part('content', 'archive-productc');                    }                    ?>                </div>                <?php porto_pagination(); ?>            </div>        <?php endif;?>        <?php wp_reset_postdata(); ?>    <?php else : ?>        <p><?php _e('Apologies, but no results were found for the requested archive.', 'porto'); ?></p>    <?php endif; ?></div><?php get_footer() ?>